00111     public string Encrypt(string plainText)
00112     {
00113         ThrowIfDisposed();
00114         ArgumentNullException.ThrowIfNull(plainText);
00115 
00116         using var activity = StartActivity();
00117         var sw = System.Diagnostics.Stopwatch.StartNew();
00118 
00119         try
00120         {
00121             using var aes = Aes.Create();
00122             aes.Key = _key;
00123             aes.IV = _iv;
00124             aes.Mode = CipherMode.CBC;
00125             aes.Padding = PaddingMode.PKCS7;
00126 
00127             using var encryptor = aes.CreateEncryptor();
00128             var plainBytes = Encoding.UTF8.GetBytes(plainText);
00129             var encryptedBytes = encryptor.TransformFinalBlock(plainBytes, 0, plainBytes.Length);
00130             var result = Convert.ToBase64String(encryptedBytes);
00131 
00132             _logger.LogDebug(
00133                 "Encrypted {InputLength} characters to {OutputLength} characters",
00134                 plainText.Length,
00135                 result.Length);
00136 
00137             RecordOperation("Encrypt", sw.ElapsedMilliseconds);
00138             ToolboxMeter.RecordEncryption(ServiceName, "AES", plainBytes.Length, _key.Length * 8);
00139             return result;
00140         }
00141         catch (Exception ex) when (ex is not ArgumentNullException)
00142         {
00143             _logger.LogError(ex, "Failed to encrypt text");
00144             throw;
00145         }
00146     }
