00158 
00159         try
00160         {
00161             using var aes = Aes.Create();
00162             aes.Key = _key;
00163             aes.IV = _iv;
00164             aes.Mode = CipherMode.CBC;
00165             aes.Padding = PaddingMode.PKCS7;
00166 
00167             using var encryptor = aes.CreateEncryptor();
00168             using var memoryStream = new MemoryStream();
