00179                 plainText.Length,
00180                 result.Length);
00181 
00182             RecordOperation("EncryptAsync", sw.ElapsedMilliseconds);
00183             ToolboxMeter.RecordEncryption(ServiceName, "AES", plainBytes.Length, _key.Length * 8);
00184             return result;
