00273                 result.Length);
00274 
00275             RecordOperation("DecryptAsync", sw.ElapsedMilliseconds);
00276             ToolboxMeter.RecordDecryption(ServiceName, "AES", outputStream.ToArray().Length, _key.Length * 8);
00277             return result;
00278         }
00279         catch (FormatException)
00280         {
00281             _logger.LogError("Failed to decrypt text: invalid Base64 format");
00282             throw;
00283         }
00284         catch (CryptographicException ex)
00285         {
00286             _logger.LogError(ex, "Failed to decrypt text asynchronously: cryptographic error");
00287             throw new FormatException("The encrypted text is invalid or corrupted.", ex);
