00311     private static void ValidateKeySize(int keySizeInBits)
00312     {
00313         if (keySizeInBits is not (128 or 192 or 256))
00314         {
00315             throw new ArgumentException(
00316                 $"Invalid AES key size: {keySizeInBits} bits. Valid sizes are 128, 192, or 256 bits.",
00317                 nameof(keySizeInBits));
00318         }
00319     }
