00326     private static void ValidateIvSize(int ivSizeInBytes)
00327     {
00328         if (ivSizeInBytes != 16)
00329         {
00330             throw new ArgumentException(
00331                 $"Invalid AES IV size: {ivSizeInBytes} bytes. IV must be exactly 16 bytes (128 bits).",
00332                 nameof(ivSizeInBytes));
