00107     public async Task UploadOneAsync(string localPath, string remotePath, bool overwrite = true, CancellationToken cancellationToken = default)
00108     {
00109         ThrowIfDisposed();
00110         ArgumentNullException.ThrowIfNull(localPath);
00111         ArgumentNullException.ThrowIfNull(remotePath);
00112 
00113         if (!File.Exists(localPath))
00114         {
00115             throw new FileNotFoundException("Local file not found.", localPath);
00116         }
00117 
00118         cancellationToken.ThrowIfCancellationRequested();
00119 
00120         using var activity = StartActivity();
00121         var sw = System.Diagnostics.Stopwatch.StartNew();
00122 
00123         try
00124         {
00125             await EnsureConnectedAsync(cancellationToken);
00126 
00127             var existsMode = overwrite ? FtpRemoteExists.Overwrite : FtpRemoteExists.Skip;
00128             var createDir = _options.AutoCreateDirectory ? FtpLocalExists.Overwrite : FtpLocalExists.Skip;
00129 
00130             var status = await _client.UploadFile(
00131                 localPath,
00132                 remotePath,
00133                 existsMode,
00134                 _options.AutoCreateDirectory,
00135                 FtpVerify.None,
00136                 null,
00137                 cancellationToken);
00138 
00139             if (status == FtpStatus.Failed)
00140             {
00141                 throw new IOException($"Failed to upload file to {remotePath}");
00142             }
00143 
00144             var fileSize = new FileInfo(localPath).Length;
00145 
00146             _logger.LogDebug(
00147                 "Uploaded {LocalPath} to {RemotePath} (status: {Status})",
00148                 localPath,
00149                 remotePath,
00150                 status);
00151 
00152             RecordOperation("UploadOne", sw.ElapsedMilliseconds);
00153             ToolboxMeter.RecordFileUpload(ServiceName, _options.Protocol.ToString(), _options.Host, fileSize);
00154         }
00155         catch (Exception ex) when (ex is not ArgumentNullException and not FileNotFoundException and not OperationCanceledException and not ObjectDisposedException)
00156         {
00157             _logger.LogError(ex, "Failed to upload {LocalPath} to {RemotePath}", localPath, remotePath);
00158             ToolboxMeter.RecordFileTransferError(ServiceName, _options.Protocol.ToString(), _options.Host, "upload", ex.GetType().Name);
00159             throw new IOException($"Failed to upload file: {ex.Message}", ex);
00160         }
00161     }
