00170     public async Task<int> UploadBatchAsync(IEnumerable<(string LocalPath, string RemotePath)> files, bool overwrite = true, CancellationToken cancellationToken = default)
00171     {
00172         ThrowIfDisposed();
00173         ArgumentNullException.ThrowIfNull(files);
00174 
00175         cancellationToken.ThrowIfCancellationRequested();
00176 
00177         using var activity = StartActivity();
00178         var sw = System.Diagnostics.Stopwatch.StartNew();
00179 
00180         var fileList = files.ToList();
00181         var successCount = 0;
00182         var exceptions = new List<Exception>();
00183 
00184         await EnsureConnectedAsync(cancellationToken);
00185 
00186         foreach (var (localPath, remotePath) in fileList)
00187         {
00188             try
00189             {
00190                 await UploadOneAsync(localPath, remotePath, overwrite, cancellationToken);
00191                 successCount++;
00192             }
00193             catch (Exception ex) when (ex is not OperationCanceledException)
00194             {
00195                 exceptions.Add(new IOException($"Failed to upload {localPath}: {ex.Message}", ex));
00196             }
00197         }
00198 
00199         RecordOperation("UploadBatch", sw.ElapsedMilliseconds);
00200 
00201         if (exceptions.Count > 0)
00202         {
00203             _logger.LogWarning(
00204                 "Batch upload completed with {SuccessCount}/{TotalCount} successes and {ErrorCount} errors",
00205                 successCount,
00206                 fileList.Count,
00207                 exceptions.Count);
00208 
00209             if (successCount == 0)
00210             {
00211                 throw new AggregateException("All file uploads failed.", exceptions);
00212             }
00213         }
00214 
00215         return successCount;
00216     }
