00225     public async Task DownloadOneAsync(string remotePath, string localPath, bool overwrite = true, CancellationToken cancellationToken = default)
00226     {
00227         ThrowIfDisposed();
00228         ArgumentNullException.ThrowIfNull(remotePath);
