00239         using var activity = StartActivity();
00240         var sw = System.Diagnostics.Stopwatch.StartNew();
00241 
00242         try
00243         {
00244             await EnsureConnectedAsync(cancellationToken);
00245 
00246             // Ensure local directory exists
00247             var localDir = Path.GetDirectoryName(localPath);
00248             if (!string.IsNullOrEmpty(localDir) && !Directory.Exists(localDir))
00249             {
00250                 Directory.CreateDirectory(localDir);
00251             }
00252 
00253             var existsMode = overwrite ? FtpLocalExists.Overwrite : FtpLocalExists.Skip;
00254 
00255             var status = await _client.DownloadFile(
00256                 localPath,
00257                 remotePath,
00258                 existsMode,
00259                 FtpVerify.None,
00260                 null,
00261                 cancellationToken);
00262 
00263             if (status == FtpStatus.Failed)
00264             {
00265                 throw new IOException($"Failed to download file from {remotePath}");
00266             }
00267 
00268             var fileSize = new FileInfo(localPath).Length;
00269 
00270             _logger.LogDebug(
00271                 "Downloaded {RemotePath} to {LocalPath} (status: {Status})",
00272                 remotePath,
00273                 localPath,
00274                 status);
00275 
00276             RecordOperation("DownloadOne", sw.ElapsedMilliseconds);
00277             ToolboxMeter.RecordFileDownload(ServiceName, _options.Protocol.ToString(), _options.Host, fileSize);
00278         }
00279         catch (Exception ex) when (ex.Message.Contains("not found", StringComparison.OrdinalIgnoreCase) ||
00280                                     ex.Message.Contains("doesn't exist", StringComparison.OrdinalIgnoreCase) ||
00281                                     ex.Message.Contains("550", StringComparison.OrdinalIgnoreCase))
00282         {
00283             throw new FileNotFoundException($"Remote file not found: {remotePath}", remotePath, ex);
00284         }
00285         catch (Exception ex) when (ex is not ArgumentNullException and not FileNotFoundException and not OperationCanceledException and not ObjectDisposedException)
