00349     protected override async ValueTask DisposeAsyncCore(CancellationToken cancellationToken)
00350     {
00351         await _connectionLock.WaitAsync(cancellationToken);
00352         try
00353         {
00354             if (_isConnected)
00355             {
00356                 await _client.Disconnect(cancellationToken);
00357                 _isConnected = false;
00358             }
00359         }
00360         finally
00361         {
00362             _connectionLock.Release();
00363         }
00364 
00365         _client.Dispose();
00366         _connectionLock.Dispose();
00367     }
