00391     private async Task EnsureConnectedAsync(CancellationToken cancellationToken)
00392     {
00393         if (_isConnected && _client.IsConnected)
00394         {
00395             return;
00396         }
00397 
00398         await _connectionLock.WaitAsync(cancellationToken);
00399         try
00400         {
00401             if (!_isConnected || !_client.IsConnected)
00402             {
00403                 await _client.Connect(cancellationToken);
00404                 _isConnected = true;
00405                 _logger.LogDebug("Connected to FTP server {Host}:{Port}", _options.Host, _options.GetEffectivePort());
00406             }
00407         }
00408         finally
00409         {
00410             _connectionLock.Release();
00411         }
00412     }
