00123     public async Task<ApiResponse> SendAsync(ApiRequest request, CancellationToken cancellationToken = default)
00124     {
00125         ThrowIfDisposed();
00126         ArgumentNullException.ThrowIfNull(request);
00127 
00128         cancellationToken.ThrowIfCancellationRequested();
00129 
00130         using var activity = StartActivity();
00131         var sw = System.Diagnostics.Stopwatch.StartNew();
00132 
00133         var retryCount = 0;
00134         HttpResponseMessage? response = null;
00135         Exception? lastException = null;
00136 
00137         while (retryCount <= _options.MaxRetries)
00138         {
00139             try
00140             {
00141                 using var httpRequest = await BuildHttpRequestAsync(request, cancellationToken);
00142 
00143                 _logger.LogDebug(
00144                     "Sending {Method} request to {Url}",
00145                     request.Method,
00146                     httpRequest.RequestUri);
00147 
00148                 response = await _httpClient.SendAsync(httpRequest, cancellationToken);
00149 
00150                 // Don't retry on success or client errors
00151                 if (response.IsSuccessStatusCode || (int)response.StatusCode < 500)
00152                 {
00153                     break;
00154                 }
00155 
00156                 lastException = new HttpRequestException(
00157                     $"Server error: {response.StatusCode}",
00158                     null,
00159                     response.StatusCode);
00160             }
00161             catch (HttpRequestException ex)
00162             {
00163                 lastException = ex;
00164             }
00165             catch (TaskCanceledException ex) when (!cancellationToken.IsCancellationRequested)
00166             {
00167                 lastException = new HttpRequestException("Request timed out", ex);
00168             }
00169 
00170             retryCount++;
00171 
00172             if (retryCount <= _options.MaxRetries)
00173             {
00174                 var delay = _options.UseExponentialBackoff
00175                     ? TimeSpan.FromMilliseconds(_options.RetryDelay.TotalMilliseconds * Math.Pow(2, retryCount - 1))
00176                     : _options.RetryDelay;
00177 
00178                 _logger.LogWarning(
00179                     "Request failed, retrying in {Delay}ms (attempt {Attempt}/{MaxRetries})",
00180                     delay.TotalMilliseconds,
00181                     retryCount,
00182                     _options.MaxRetries);
00183 
00184                 await Task.Delay(delay, cancellationToken);
00185             }
00186         }
00187 
00188         if (response is null)
00189         {
00190             throw lastException ?? new HttpRequestException("Request failed after all retries");
00191         }
00192 
00193         var apiResponse = await BuildApiResponseAsync(response, sw.Elapsed, cancellationToken);
00194 
00195         RecordOperation("Send", sw.ElapsedMilliseconds);
00196         RecordApiRequest(request, apiResponse);
00197 
00198         _logger.LogDebug(
00199             "Received {StatusCode} response in {Duration}ms",
00200             apiResponse.StatusCode,
00201             sw.ElapsedMilliseconds);
00202 
00203         return apiResponse;
00204     }
