00238     private HttpClient CreateHttpClient()
00239     {
00240         var handler = new HttpClientHandler
00241         {
00242             AllowAutoRedirect = _options.FollowRedirects,
00243             MaxAutomaticRedirections = _options.MaxRedirects
00244         };
00245 
00246         // SSL certificate validation
00247         if (!_options.ValidateCertificate)
00248         {
00249             handler.ServerCertificateCustomValidationCallback = (_, _, _, _) => true;
00250         }
00251 
00252         // Client certificate authentication
00253         if (_options.AuthenticationMode == ApiAuthenticationMode.Certificate)
00254         {
00255             var certificate = GetClientCertificate();
00256             if (certificate is not null)
00257             {
00258                 handler.ClientCertificates.Add(certificate);
00259             }
00260         }
00261 
00262         var client = new HttpClient(handler)
00263         {
00264             Timeout = _options.Timeout
00265         };
00266 
00267         ConfigureHttpClient(client);
00268 
00269         return client;
00270     }
