00325     private async Task<HttpRequestMessage> BuildHttpRequestAsync(
00326         ApiRequest request,
00327         CancellationToken cancellationToken)
00328     {
00329         var url = request.BuildUrl();
00330 
00331         // Handle API key in query string
00332         if (_options.AuthenticationMode == ApiAuthenticationMode.ApiKey &&
00333             _options.ApiKeyLocation == ApiKeyLocation.QueryString &&
00334             !string.IsNullOrEmpty(_options.ApiKey))
00335         {
00336             var separator = url.Contains('?') ? "&" : "?";
00337             url = $"{url}{separator}{Uri.EscapeDataString(_options.ApiKeyName)}={Uri.EscapeDataString(_options.ApiKey)}";
00338         }
00339 
00340         var httpRequest = new HttpRequestMessage(new HttpMethod(request.Method), url)
00341         {
00342             Content = request.Content
00343         };
00344 
00345         // Add request headers
00346         foreach (var (name, value) in request.Headers)
00347         {
00348             httpRequest.Headers.TryAddWithoutValidation(name, value);
00349         }
00350 
00351         // Add authentication
00352         await AddAuthenticationAsync(httpRequest, cancellationToken);
00353 
00354         return httpRequest;
00355     }
