00362     private async Task AddAuthenticationAsync(HttpRequestMessage request, CancellationToken cancellationToken)
00363     {
00364         switch (_options.AuthenticationMode)
00365         {
00366             case ApiAuthenticationMode.BearerToken:
00367                 if (!string.IsNullOrEmpty(_options.BearerToken))
00368                 {
00369                     request.Headers.Authorization = new AuthenticationHeaderValue("Bearer", _options.BearerToken);
00370                 }
00371                 break;
00372 
00373             case ApiAuthenticationMode.Basic:
00374                 if (!string.IsNullOrEmpty(_options.Username))
00375                 {
00376                     var credentials = Convert.ToBase64String(
00377                         Encoding.ASCII.GetBytes($"{_options.Username}:{_options.Password ?? string.Empty}"));
00378                     request.Headers.Authorization = new AuthenticationHeaderValue("Basic", credentials);
00379                 }
00380                 break;
00381 
00382             case ApiAuthenticationMode.ApiKey:
00383                 if (_options.ApiKeyLocation == ApiKeyLocation.Header && !string.IsNullOrEmpty(_options.ApiKey))
00384                 {
00385                     request.Headers.TryAddWithoutValidation(_options.ApiKeyName, _options.ApiKey);
00386                 }
00387                 break;
00388 
00389             case ApiAuthenticationMode.OAuth2ClientCredentials:
00390                 var token = await GetOAuth2TokenAsync(cancellationToken);
00391                 if (!string.IsNullOrEmpty(token))
00392                 {
00393                     request.Headers.Authorization = new AuthenticationHeaderValue("Bearer", token);
00394                 }
00395                 break;
00396 
00397             case ApiAuthenticationMode.Certificate:
00398                 // Certificate is added at the handler level
00399                 break;
00400 
00401             case ApiAuthenticationMode.Anonymous:
00402             default:
00403                 // No authentication
00404                 break;
00405         }
00406     }
