00413     private async Task<string?> GetOAuth2TokenAsync(CancellationToken cancellationToken)
00414     {
00415         // Check cached token
00416         if (!string.IsNullOrEmpty(_oauth2Token) && DateTime.UtcNow < _oauth2TokenExpiry)
00417         {
00418             return _oauth2Token;
00419         }
00420 
00421         await _oauth2Lock.WaitAsync(cancellationToken);
00422         try
00423         {
00424             // Double-check after acquiring lock
00425             if (!string.IsNullOrEmpty(_oauth2Token) && DateTime.UtcNow < _oauth2TokenExpiry)
00426             {
00427                 return _oauth2Token;
00428             }
00429 
00430             if (string.IsNullOrEmpty(_options.OAuth2TokenUrl) ||
00431                 string.IsNullOrEmpty(_options.OAuth2ClientId) ||
00432                 string.IsNullOrEmpty(_options.OAuth2ClientSecret))
00433             {
00434                 _logger.LogWarning("OAuth2 credentials not configured");
00435                 return null;
00436             }
00437 
00438             using var tokenClient = new HttpClient();
00439 
00440             var tokenRequest = new Dictionary<string, string>
00441             {
00442                 ["grant_type"] = "client_credentials",
00443                 ["client_id"] = _options.OAuth2ClientId,
00444                 ["client_secret"] = _options.OAuth2ClientSecret
00445             };
00446 
00447             if (!string.IsNullOrEmpty(_options.OAuth2Scopes))
00448             {
00449                 tokenRequest["scope"] = _options.OAuth2Scopes;
00450             }
00451 
00452             using var content = new FormUrlEncodedContent(tokenRequest);
00453             using var response = await tokenClient.PostAsync(_options.OAuth2TokenUrl, content, cancellationToken);
00454 
00455             response.EnsureSuccessStatusCode();
00456 
00457             var json = await response.Content.ReadAsStringAsync(cancellationToken);
00458             using var doc = JsonDocument.Parse(json);
00459 
00460             _oauth2Token = doc.RootElement.GetProperty("access_token").GetString();
00461 
00462             if (doc.RootElement.TryGetProperty("expires_in", out var expiresIn))
00463             {
00464                 var expiresInSeconds = expiresIn.GetInt32();
00465                 _oauth2TokenExpiry = DateTime.UtcNow.AddSeconds(expiresInSeconds - 60); // 1 minute buffer
00466             }
00467             else
00468             {
00469                 _oauth2TokenExpiry = DateTime.UtcNow.AddMinutes(55); // Default 55 minutes
00470             }
00471 
00472             _logger.LogDebug("Obtained OAuth2 token, expires at {Expiry}", _oauth2TokenExpiry);
00473 
00474             return _oauth2Token;
00475         }
00476         finally
00477         {
00478             _oauth2Lock.Release();
00479         }
00480     }
