00489     private static async Task<ApiResponse> BuildApiResponseAsync(
00490         HttpResponseMessage response,
00491         TimeSpan duration,
00492         CancellationToken cancellationToken)
00493     {
00494         var body = await response.Content.ReadAsStringAsync(cancellationToken);
00495         var bodyBytes = await response.Content.ReadAsByteArrayAsync(cancellationToken);
00496 
00497         var headers = new Dictionary<string, IEnumerable<string>>();
00498         foreach (var header in response.Headers)
00499         {
00500             headers[header.Key] = header.Value;
00501         }
00502         foreach (var header in response.Content.Headers)
00503         {
00504             headers[header.Key] = header.Value;
00505         }
00506 
00507         return new ApiResponse
00508         {
00509             StatusCode = response.StatusCode,
00510             ReasonPhrase = response.ReasonPhrase,
00511             Body = body,
00512             BodyBytes = bodyBytes,
00513             Headers = headers,
00514             ContentType = response.Content.Headers.ContentType?.ToString(),
00515             ContentLength = response.Content.Headers.ContentLength,
00516             Duration = duration
00517         };
00518     }
