00120     public RsaCryptographyService(
00121         X509Certificate2 certificate,
00122         RsaPaddingMode paddingMode,
00123         ILogger<RsaCryptographyService> logger)
00124         : base("RsaCryptographyService", logger)
00125     {
00126         ArgumentNullException.ThrowIfNull(certificate);
00127         ArgumentNullException.ThrowIfNull(logger);
00128 
00129         _logger = logger;
00130         _padding = GetEncryptionPadding(paddingMode);
00131 
00132         var publicRsa = certificate.GetRSAPublicKey()
00133             ?? throw new ArgumentException("Certificate does not contain an RSA public key.", nameof(certificate));
00134 
00135         _rsa = RSA.Create();
00136 
00137         try
00138         {
00139             // Import public key
00140             _rsa.ImportSubjectPublicKeyInfo(publicRsa.ExportSubjectPublicKeyInfo(), out _);
00141 
00142             // Import private key if available
00143             if (certificate.HasPrivateKey)
00144             {
00145                 var privateRsa = certificate.GetRSAPrivateKey();
00146                 if (privateRsa is not null)
00147                 {
00148                     _rsa.ImportPkcs8PrivateKey(privateRsa.ExportPkcs8PrivateKey(), out _);
00149                     _hasPrivateKey = true;
00150                 }
00151             }
00152         }
00153         catch (Exception ex) when (ex is not ArgumentException)
00154         {
00155             _rsa.Dispose();
00156             throw new CryptographicException("Failed to import RSA key(s) from certificate.", ex);
00157         }
00158 
00159         _logger.LogDebug(
00160             "RsaCryptographyService initialized from certificate with {KeySize}-bit key, padding: {Padding}, has private key: {HasPrivateKey}",
00161             _rsa.KeySize,
00162             paddingMode,
00163             _hasPrivateKey);
