00231     public static RsaKeyPair GenerateCertificate(
00232         RsaKeySize keySize,
00233         string subjectName,
00234         TimeSpan validityPeriod,
00235         bool includePrivateKey = true)
00236     {
00237         ArgumentNullException.ThrowIfNull(subjectName);
00238         ValidateKeySize((int)keySize);
00239 
00240         using var rsa = RSA.Create((int)keySize);
00241 
00242         var request = new CertificateRequest(
00243             subjectName,
00244             rsa,
00245             HashAlgorithmName.SHA256,
00246             RSASignaturePadding.Pkcs1);
00247 
00248         var notBefore = DateTimeOffset.UtcNow;
00249         var notAfter = notBefore.Add(validityPeriod);
00250 
00251         using var certWithPrivateKey = request.CreateSelfSigned(notBefore, notAfter);
00252 
00253         X509Certificate2 resultCert;
00254         if (includePrivateKey)
00255         {
00256             // Export and re-import to ensure private key is exportable
00257             var pfxBytes = certWithPrivateKey.Export(X509ContentType.Pfx);
00258             resultCert = X509CertificateLoader.LoadPkcs12(pfxBytes, null, X509KeyStorageFlags.Exportable);
00259         }
00260         else
00261         {
00262             // Export only the public certificate
00263             var certBytes = certWithPrivateKey.Export(X509ContentType.Cert);
00264             resultCert = X509CertificateLoader.LoadCertificate(certBytes);
00265         }
00266 
00267         return RsaKeyPair.FromCertificate(resultCert);
00268     }
