00294     public string Encrypt(string plainText)
00295     {
00296         ThrowIfDisposed();
00297         ArgumentNullException.ThrowIfNull(plainText);
00298 
00299         using var activity = StartActivity();
00300         var sw = System.Diagnostics.Stopwatch.StartNew();
00301 
00302         try
00303         {
00304             var plainBytes = Encoding.UTF8.GetBytes(plainText);
00305             var maxDataLength = GetMaxDataLength();
00306 
00307             if (plainBytes.Length > maxDataLength)
00308             {
00309                 throw new CryptographicException(
00310                     $"Data too large for RSA encryption. Maximum size is {maxDataLength} bytes, but data is {plainBytes.Length} bytes. " +
00311                     "Consider using hybrid encryption (RSA + AES) for larger data.");
00312             }
00313 
00314             var encryptedBytes = _rsa.Encrypt(plainBytes, _padding);
00315             var result = Convert.ToBase64String(encryptedBytes);
00316 
00317             _logger.LogDebug(
00318                 "Encrypted {InputLength} bytes to {OutputLength} characters",
00319                 plainBytes.Length,
00320                 result.Length);
00321 
00322             RecordOperation("Encrypt", sw.ElapsedMilliseconds);
00323             ToolboxMeter.RecordEncryption(ServiceName, "RSA", plainBytes.Length, _rsa.KeySize);
00324             return result;
00325         }
00326         catch (Exception ex) when (ex is not ArgumentNullException and not CryptographicException)
00327         {
00328             _logger.LogError(ex, "Failed to encrypt text");
00329             throw new CryptographicException("RSA encryption failed.", ex);
00330         }
00331     }
