00348     public string Decrypt(string encryptedText)
00349     {
00350         ThrowIfDisposed();
00351         ArgumentNullException.ThrowIfNull(encryptedText);
00352 
00353         if (!_hasPrivateKey)
00354         {
00355             throw new InvalidOperationException(
00356                 "Cannot decrypt without a private key. Ensure the service was initialized with a private key.");
00357         }
00358 
00359         using var activity = StartActivity();
00360         var sw = System.Diagnostics.Stopwatch.StartNew();
00361 
00362         try
00363         {
00364             var encryptedBytes = Convert.FromBase64String(encryptedText);
00365             var decryptedBytes = _rsa.Decrypt(encryptedBytes, _padding);
00366             var result = Encoding.UTF8.GetString(decryptedBytes);
00367 
00368             _logger.LogDebug(
00369                 "Decrypted {InputLength} characters to {OutputLength} bytes",
00370                 encryptedText.Length,
00371                 decryptedBytes.Length);
00372 
00373             RecordOperation("Decrypt", sw.ElapsedMilliseconds);
00374             ToolboxMeter.RecordDecryption(ServiceName, "RSA", decryptedBytes.Length, _rsa.KeySize);
00375             return result;
00376         }
00377         catch (FormatException)
00378         {
00379             _logger.LogError("Failed to decrypt text: invalid Base64 format");
00380             throw;
00381         }
00382         catch (CryptographicException ex)
00383         {
00384             _logger.LogError(ex, "Failed to decrypt text: cryptographic error");
00385             throw new FormatException("The encrypted text is invalid or corrupted.", ex);
00386         }
00387         catch (Exception ex) when (ex is not ArgumentNullException and not FormatException and not InvalidOperationException)
00388         {
