00418     private static RSAEncryptionPadding GetEncryptionPadding(RsaPaddingMode mode) => mode switch
00419     {
00420         RsaPaddingMode.Pkcs1 => RSAEncryptionPadding.Pkcs1,
00421         RsaPaddingMode.OaepSha1 => RSAEncryptionPadding.OaepSHA1,
00422         RsaPaddingMode.OaepSha256 => RSAEncryptionPadding.OaepSHA256,
00423         RsaPaddingMode.OaepSha384 => RSAEncryptionPadding.OaepSHA384,
00424         RsaPaddingMode.OaepSha512 => RSAEncryptionPadding.OaepSHA512,
00425         _ => throw new ArgumentOutOfRangeException(nameof(mode), mode, "Invalid padding mode.")
00426     };
