00433     private static void ValidateKeySize(int keySizeInBits)
00434     {
00435         if (keySizeInBits is not (512 or 1024 or 2048 or 4096 or 8192 or 16384))
00436         {
00437             throw new ArgumentException(
00438                 $"Invalid RSA key size: {keySizeInBits} bits. Valid sizes are 512, 1024, 2048, 4096, 8192, or 16384 bits.",
00439                 nameof(keySizeInBits));
00440         }
00441     }
