00447     private int GetMaxDataLength()
00448     {
00449         var keyBytes = _rsa.KeySize / 8;
00450 
00451         // OAEP overhead depends on hash size
00452         // Formula: keyBytes - 2 * hashBytes - 2
00453         return _padding.OaepHashAlgorithm.Name switch
00454         {
00455             "SHA1" => keyBytes - 42,    // 2 * 20 + 2 = 42
00456             "SHA256" => keyBytes - 66,  // 2 * 32 + 2 = 66
00457             "SHA384" => keyBytes - 98,  // 2 * 48 + 2 = 98
00458             "SHA512" => keyBytes - 130, // 2 * 64 + 2 = 130
00459             _ => keyBytes - 11          // PKCS#1 v1.5 overhead is 11 bytes
00460         };
00461     }
