00095     public void UploadOne(string localPath, string remotePath, bool overwrite = true)
00096     {
00097         ThrowIfDisposed();
00098         ArgumentNullException.ThrowIfNull(localPath);
00099         ArgumentNullException.ThrowIfNull(remotePath);
00100 
00101         if (!File.Exists(localPath))
00102         {
00103             throw new FileNotFoundException("Local file not found.", localPath);
00104         }
00105 
00106         using var activity = StartActivity();
00107         var sw = System.Diagnostics.Stopwatch.StartNew();
00108 
00109         try
00110         {
00111             EnsureConnected();
00112 
00113             if (!overwrite && _client.Exists(remotePath))
00114             {
00115                 _logger.LogDebug("Skipping upload, remote file exists: {RemotePath}", remotePath);
00116                 return;
00117             }
00118 
00119             // Create remote directory if needed
00120             if (_options.AutoCreateDirectory)
00121             {
00122                 var remoteDir = GetDirectoryPath(remotePath);
00123                 if (!string.IsNullOrEmpty(remoteDir))
00124                 {
00125                     CreateDirectoryRecursive(remoteDir);
00126                 }
00127             }
00128 
00129             using var fileStream = File.OpenRead(localPath);
00130             var fileSize = fileStream.Length;
00131             _client.UploadFile(fileStream, remotePath, overwrite);
00132 
00133             _logger.LogDebug("Uploaded {LocalPath} to {RemotePath}", localPath, remotePath);
00134             RecordOperation("UploadOne", sw.ElapsedMilliseconds);
00135             ToolboxMeter.RecordFileUpload(ServiceName, "SFTP", _options.Host, fileSize);
00136         }
00137         catch (SftpPathNotFoundException ex)
00138         {
00139             _logger.LogError(ex, "Remote path not found: {RemotePath}", remotePath);
00140             ToolboxMeter.RecordFileTransferError(ServiceName, "SFTP", _options.Host, "upload", ex.GetType().Name);
00141             throw new IOException($"Remote path not found: {remotePath}", ex);
00142         }
00143         catch (SshException ex)
00144         {
00145             _logger.LogError(ex, "Failed to upload {LocalPath} to {RemotePath}", localPath, remotePath);
00146             ToolboxMeter.RecordFileTransferError(ServiceName, "SFTP", _options.Host, "upload", ex.GetType().Name);
00147             throw new IOException($"Failed to upload file: {ex.Message}", ex);
00148         }
