00211     public int UploadBatch(IEnumerable<(string LocalPath, string RemotePath)> files, bool overwrite = true)
00212     {
00213         ThrowIfDisposed();
00214         ArgumentNullException.ThrowIfNull(files);
00215 
00216         using var activity = StartActivity();
00217         var sw = System.Diagnostics.Stopwatch.StartNew();
00218 
00219         var fileList = files.ToList();
00220         var successCount = 0;
00221         var exceptions = new List<Exception>();
00222 
00223         EnsureConnected();
00224 
00225         foreach (var (localPath, remotePath) in fileList)
00226         {
00227             try
00228             {
00229                 UploadOne(localPath, remotePath, overwrite);
00230                 successCount++;
00231             }
00232             catch (Exception ex) when (ex is not OperationCanceledException)
00233             {
00234                 exceptions.Add(new IOException($"Failed to upload {localPath}: {ex.Message}", ex));
00235             }
00236         }
00237 
00238         RecordOperation("UploadBatch", sw.ElapsedMilliseconds);
00239 
00240         if (exceptions.Count > 0)
00241         {
00242             _logger.LogWarning(
00243                 "Batch upload completed with {SuccessCount}/{TotalCount} successes and {ErrorCount} errors",
00244                 successCount,
00245                 fileList.Count,
00246                 exceptions.Count);
00247 
00248             if (successCount == 0)
00249             {
00250                 throw new AggregateException("All file uploads failed.", exceptions);
00251             }
00252         }
00253 
00254         return successCount;
00255     }
