00512     protected override async ValueTask DisposeAsyncCore(CancellationToken cancellationToken)
00513     {
00514         await _connectionLock.WaitAsync(cancellationToken);
00515         try
00516         {
00517             if (_client.IsConnected)
00518             {
00519                 _client.Disconnect();
00520             }
00521         }
00522         finally
00523         {
00524             _connectionLock.Release();
00525         }
00526 
00527         _client.Dispose();
00528         _connectionLock.Dispose();
00529     }
