00535     private ConnectionInfo CreateConnectionInfo()
00536     {
00537         var authMethods = new List<AuthenticationMethod>();
00538 
00539         // Private key authentication
00540         if (!string.IsNullOrEmpty(_options.PrivateKeyPath) || !string.IsNullOrEmpty(_options.PrivateKeyContent))
00541         {
00542             PrivateKeyFile keyFile;
00543 
00544             if (!string.IsNullOrEmpty(_options.PrivateKeyContent))
00545             {
00546                 using var keyStream = new MemoryStream(System.Text.Encoding.UTF8.GetBytes(_options.PrivateKeyContent));
00547                 keyFile = string.IsNullOrEmpty(_options.PrivateKeyPassphrase)
00548                     ? new PrivateKeyFile(keyStream)
00549                     : new PrivateKeyFile(keyStream, _options.PrivateKeyPassphrase);
00550             }
00551             else
00552             {
00553                 keyFile = string.IsNullOrEmpty(_options.PrivateKeyPassphrase)
00554                     ? new PrivateKeyFile(_options.PrivateKeyPath!)
00555                     : new PrivateKeyFile(_options.PrivateKeyPath!, _options.PrivateKeyPassphrase);
00556             }
00557 
00558             authMethods.Add(new PrivateKeyAuthenticationMethod(_options.Username, keyFile));
00559         }
00560 
00561         // Password authentication
00562         if (!string.IsNullOrEmpty(_options.Password))
00563         {
00564             authMethods.Add(new PasswordAuthenticationMethod(_options.Username, _options.Password));
00565         }
00566 
00567         if (authMethods.Count == 0)
00568         {
00569             throw new ArgumentException("No authentication method configured. Provide either a password or private key.");
00570         }
00571 
00572         return new ConnectionInfo(
00573             _options.Host,
00574             _options.GetEffectivePort(),
00575             _options.Username,
00576             authMethods.ToArray())
00577         {
00578             Timeout = _options.ConnectionTimeout
00579         };
00580     }
