00585     private void EnsureConnected()
00586     {
00587         if (_client.IsConnected)
00588         {
00589             return;
00590         }
00591 
00592         _connectionLock.Wait();
00593         try
00594         {
00595             if (!_client.IsConnected)
00596             {
00597                 _client.Connect();
00598                 _logger.LogDebug("Connected to SFTP server {Host}:{Port}", _options.Host, _options.GetEffectivePort());
00599             }
00600         }
00601         finally
00602         {
00603             _connectionLock.Release();
00604         }
00605     }
