00637     private void CreateDirectoryRecursive(string path)
00638     {
00639         var current = string.Empty;
00640 
00641         foreach (var segment in path.Split('/', StringSplitOptions.RemoveEmptyEntries))
00642         {
00643             current = $"{current}/{segment}";
00644 
00645             if (!_client.Exists(current))
00646             {
00647                 _client.CreateDirectory(current);
00648             }
00649         }
00650     }
