00109     public async Task SendMailAsync(EmailMessage message, CancellationToken cancellationToken = default)
00110     {
00111         ThrowIfDisposed();
00112         ArgumentNullException.ThrowIfNull(message);
00113 
00114         message.Validate();
00115 
00116         cancellationToken.ThrowIfCancellationRequested();
00117 
00118         using var activity = StartActivity();
00119         var sw = System.Diagnostics.Stopwatch.StartNew();
00120 
00121         try
00122         {
00123             await EnsureConnectedAsync(cancellationToken);
00124 
00125             var mimeMessage = BuildMimeMessage(message);
00126 
00127             await _client.SendAsync(mimeMessage, cancellationToken);
00128 
00129             _logger.LogDebug(
00130                 "Email sent successfully to {RecipientCount} recipient(s): Subject='{Subject}'",
00131                 message.TotalRecipients,
00132                 message.Subject);
00133 
00134             RecordOperation("SendMail", sw.ElapsedMilliseconds);
00135             RecordEmailSent(message);
00136         }
00137         catch (SmtpCommandException ex)
00138         {
00139             _logger.LogError(
00140                 ex,
00141                 "SMTP command error while sending email: {StatusCode} - {Message}",
00142                 ex.StatusCode,
00143                 ex.Message);
00144 
00145             throw new InvalidOperationException($"SMTP error: {ex.Message}", ex);
00146         }
00147         catch (SmtpProtocolException ex)
00148         {
00149             _logger.LogError(ex, "SMTP protocol error while sending email");
00150             throw new InvalidOperationException($"SMTP protocol error: {ex.Message}", ex);
00151         }
00152         catch (Exception ex) when (ex is not ArgumentNullException
00153                                     and not ArgumentException
00154                                     and not OperationCanceledException
00155                                     and not ObjectDisposedException
00156                                     and not InvalidOperationException)
00157         {
00158             _logger.LogError(ex, "Failed to send email");
00159             throw new InvalidOperationException($"Failed to send email: {ex.Message}", ex);
00160         }
00161     }
