00164     protected override async ValueTask DisposeAsyncCore(CancellationToken cancellationToken)
00165     {
00166         await _connectionLock.WaitAsync(cancellationToken);
00167         try
00168         {
00169             if (_isConnected && _client.IsConnected)
00170             {
00171                 await _client.DisconnectAsync(true, cancellationToken);
00172                 _isConnected = false;
00173             }
00174         }
00175         finally
00176         {
00177             _connectionLock.Release();
00178         }
00179 
00180         _client.Dispose();
00181         _connectionLock.Dispose();
00182     }
