00189     private MimeMessage BuildMimeMessage(EmailMessage message)
00190     {
00191         var mimeMessage = new MimeMessage();
00192 
00193         // From address
00194         var from = message.From ?? _options.DefaultFrom
00195             ?? throw new ArgumentException("No sender address specified and no default configured.");
00196 
00197         mimeMessage.From.Add(new MailboxAddress(from.DisplayName, from.Address));
00198 
00199         // Reply-To
00200         var replyTo = message.ReplyTo ?? _options.DefaultReplyTo;
00201         if (replyTo is not null)
00202         {
00203             mimeMessage.ReplyTo.Add(new MailboxAddress(replyTo.DisplayName, replyTo.Address));
00204         }
00205 
00206         // Recipients
00207         foreach (var to in message.To)
00208         {
00209             mimeMessage.To.Add(new MailboxAddress(to.DisplayName, to.Address));
00210         }
00211 
00212         foreach (var cc in message.Cc)
00213         {
00214             mimeMessage.Cc.Add(new MailboxAddress(cc.DisplayName, cc.Address));
00215         }
00216 
00217         foreach (var bcc in message.Bcc)
00218         {
00219             mimeMessage.Bcc.Add(new MailboxAddress(bcc.DisplayName, bcc.Address));
00220         }
00221 
00222         // Subject
00223         mimeMessage.Subject = message.Subject;
00224 
00225         // Priority
00226         mimeMessage.Priority = message.Priority switch
00227         {
00228             Options.EmailPriority.Low => MessagePriority.NonUrgent,
00229             Options.EmailPriority.High => MessagePriority.Urgent,
00230             _ => MessagePriority.Normal
00231         };
00232 
00233         // Custom headers
00234         foreach (var (name, value) in message.Headers)
00235         {
00236             mimeMessage.Headers.Add(name, value);
00237         }
00238 
00239         // Body and attachments
00240         mimeMessage.Body = BuildMessageBody(message);
00241 
00242         return mimeMessage;
00243     }
