00250     private static MimeEntity BuildMessageBody(EmailMessage message)
00251     {
00252         // Create the body part
00253         var textFormat = message.IsBodyHtml ? TextFormat.Html : TextFormat.Plain;
00254         var textPart = new TextPart(textFormat)
00255         {
00256             Text = message.Body
00257         };
00258 
00259         // If HTML with explicit plain text, create multipart/alternative
00260         MimeEntity bodyPart;
00261         if (message.IsBodyHtml && !string.IsNullOrEmpty(message.PlainTextBody))
00262         {
00263             var alternative = new Multipart("alternative")
00264             {
00265                 new TextPart(TextFormat.Plain) { Text = message.PlainTextBody },
00266                 textPart
00267             };
00268             bodyPart = alternative;
00269         }
00270         else
00271         {
00272             bodyPart = textPart;
00273         }
00274 
00275         // Handle attachments
00276         if (message.Attachments.Count == 0)
00277         {
00278             return bodyPart;
00279         }
00280 
00281         // Separate inline and regular attachments
00282         var inlineAttachments = message.Attachments.Where(a => a.IsInline).ToList();
00283         var regularAttachments = message.Attachments.Where(a => !a.IsInline).ToList();
00284 
00285         // If we have inline attachments and HTML body, wrap in multipart/related
00286         if (inlineAttachments.Count > 0 && message.IsBodyHtml)
00287         {
00288             var related = new Multipart("related") { bodyPart };
00289 
00290             foreach (var attachment in inlineAttachments)
00291             {
00292                 related.Add(CreateMimePart(attachment));
00293             }
00294 
00295             bodyPart = related;
00296         }
00297 
00298         // If we have regular attachments, wrap in multipart/mixed
00299         if (regularAttachments.Count > 0)
00300         {
00301             var mixed = new Multipart("mixed") { bodyPart };
00302 
00303             foreach (var attachment in regularAttachments)
00304             {
00305                 mixed.Add(CreateMimePart(attachment));
00306             }
00307 
00308             return mixed;
00309         }
00310 
00311         return bodyPart;
00312     }
