00319     private static MimePart CreateMimePart(EmailAttachment attachment)
00320     {
00321         var contentType = ContentType.Parse(attachment.ContentType);
00322         var mimePart = new MimePart(contentType)
00323         {
00324             FileName = attachment.FileName,
00325             ContentDisposition = new ContentDisposition(
00326                 attachment.IsInline
00327                     ? ContentDisposition.Inline
00328                     : ContentDisposition.Attachment),
00329             ContentTransferEncoding = ContentEncoding.Base64
00330         };
00331 
00332         if (attachment.IsInline && !string.IsNullOrEmpty(attachment.ContentId))
00333         {
00334             mimePart.ContentId = attachment.ContentId;
00335         }
00336 
00337         // Set content
00338         if (attachment.Content is not null)
00339         {
00340             mimePart.Content = new MimeContent(new MemoryStream(attachment.Content));
00341         }
00342         else if (!string.IsNullOrEmpty(attachment.FilePath))
00343         {
00344             mimePart.Content = new MimeContent(File.OpenRead(attachment.FilePath));
00345         }
00346 
00347         return mimePart;
00348     }
