00354     private async Task EnsureConnectedAsync(CancellationToken cancellationToken)
00355     {
00356         if (_isConnected && _client.IsConnected)
00357         {
00358             return;
00359         }
00360 
00361         await _connectionLock.WaitAsync(cancellationToken);
00362         try
00363         {
00364             if (_isConnected && _client.IsConnected)
00365             {
00366                 return;
00367             }
00368 
00369             // Convert security mode to SecureSocketOptions
00370             var secureSocketOptions = _options.SecurityMode switch
00371             {
00372                 SmtpSecurityMode.None => SecureSocketOptions.None,
00373                 SmtpSecurityMode.StartTls => SecureSocketOptions.StartTls,
00374                 SmtpSecurityMode.StartTlsWhenAvailable => SecureSocketOptions.StartTlsWhenAvailable,
00375                 SmtpSecurityMode.SslOnConnect => SecureSocketOptions.SslOnConnect,
00376                 _ => SecureSocketOptions.Auto
00377             };
00378 
00379             await _client.ConnectAsync(
00380                 _options.Host,
00381                 _options.Port,
00382                 secureSocketOptions,
00383                 cancellationToken);
00384 
00385             _logger.LogDebug(
00386                 "Connected to SMTP server {Host}:{Port}",
00387                 _options.Host,
00388                 _options.Port);
00389 
00390             // Authenticate if credentials are configured
00391             if (_options.RequiresAuthentication)
00392             {
00393                 await AuthenticateAsync(cancellationToken);
00394             }
00395 
00396             _isConnected = true;
00397         }
00398         catch (Exception ex) when (ex is not OperationCanceledException)
00399         {
00400             _logger.LogError(ex, "Failed to connect to SMTP server {Host}:{Port}", _options.Host, _options.Port);
00401             throw new InvalidOperationException($"Failed to connect to SMTP server: {ex.Message}", ex);
00402         }
00403         finally
00404         {
00405             _connectionLock.Release();
00406         }
00407     }
