00413     private async Task AuthenticateAsync(CancellationToken cancellationToken)
00414     {
00415         try
00416         {
00417             if (!string.IsNullOrEmpty(_options.OAuth2AccessToken))
00418             {
00419                 // OAuth2 authentication
00420                 var oauth2 = new SaslMechanismOAuth2(_options.Username!, _options.OAuth2AccessToken);
00421                 await _client.AuthenticateAsync(oauth2, cancellationToken);
00422 
00423                 _logger.LogDebug("Authenticated with SMTP server using OAuth2");
00424             }
00425             else if (!string.IsNullOrEmpty(_options.Username))
00426             {
00427                 // Password authentication
00428                 await _client.AuthenticateAsync(
00429                     _options.Username,
00430                     _options.Password ?? string.Empty,
00431                     cancellationToken);
00432 
00433                 _logger.LogDebug("Authenticated with SMTP server using password");
00434             }
00435         }
00436         catch (AuthenticationException ex)
00437         {
00438             _logger.LogError(ex, "SMTP authentication failed");
00439             throw new InvalidOperationException($"SMTP authentication failed: {ex.Message}", ex);
00440         }
00441     }
